import React, { useState, useEffect } from 'react';
import PopulationPyramidChart from './PopulationPyramidChart';
import 'bootstrap/dist/css/bootstrap.min.css';
import axios from 'axios';

function App() {
  const [selectedYear, setSelectedYear] = useState('2022');
  const [apiUrls, setApiUrls] = useState<{ ano: string; url: string; }[]>([]);

  useEffect(() => {
    // Função assíncrona para buscar os dados do endpoint
    const fetchData = async () => {
      try {
        const response = await axios.get('http://localhost:3333/censo');
        // Preenche o array apiUrls com os dados recebidos
        setApiUrls(response.data.map((censo: { year: string; url: string; }) => ({
          ano: censo.year,
          url: censo.url
        })));
      } catch (error) {
        console.error('Erro ao buscar os dados do censo:', error);
      }
    };

    fetchData(); // Chama a função para buscar os dados quando o componente montar
  }, []); // Array vazio para garantir que a requisição seja feita apenas uma vez, quando o componente montar


  const handleYearChange = (event: React.ChangeEvent<HTMLSelectElement>) => {
    setSelectedYear(event.target.value);
  };

  // const apiUrls: {ano:string, url:string}[] = [
  //   {
  //     "ano": "2022",
  //     "url": "https://servicodados.ibge.gov.br/api/v3/agregados/9514/periodos/-6/variaveis/93?localidades=N1[all]&classificacao=2[4,5]|287[93070,93084,93085,93086,93087,93088,93089,93090,93091,93092,93093,93094,93095,93096,93097,93098,49108,49109,60040,60041,6653]|286[113635]"
  //   },
  //   {
  //     "ano": "2010",
  //     "url": "https://servicodados.ibge.gov.br/api/v3/agregados/200/periodos/2010/variaveis/93?localidades=N1[all]&classificacao=2[4,5]|1[0]|58[1140,1141,1142,1143,1144,1145,1146,1147,1148,1149,1150,1151,1152,1153,1154,1155,6802,6803,92963,92964,92965]"
  //   }
  // ];

  return (
    <div className="container mt-5">
      <div className="row justify-content-center">
        <div className="col-md-6">
          <h1 className="mb-4">Brazilian Age Pyramid</h1>

          {/* Select para escolher o ano */}
          <div className="mb-3">
            <label htmlFor="yearSelect" className="form-label">Select Year:</label>
            <select id="yearSelect" className="form-select" value={selectedYear} onChange={handleYearChange}>
            {apiUrls.map((obj) => (
                <option key={obj.ano} value={obj.ano}>
                  {obj.ano}
                </option>
              ))}
            </select>
          </div>

          {/* PopulationPyramidChart com URL dinâmico baseado no ano selecionado */}
          <PopulationPyramidChart
            width="800px"
            height="600px"
            apiUrl={apiUrls.find(obj => obj.ano === selectedYear)?.url ?? ''}
            ano = {selectedYear}
          />
        </div>
      </div>
    </div>
  );
}

export default App;
